import React, { useState } from 'react';
import { Droppable } from 'react-beautiful-dnd';
import Card from './Card';
import './Board.css';

function Board({ board, addNewCard, deleteCard, deleteBoard }) {
  const [isAddingCard, setIsAddingCard] = useState(false);
  const [newCardTitle, setNewCardTitle] = useState('');
  const [showBoardMenu, setShowBoardMenu] = useState(false);

  const handleAddCard = () => {
    if (newCardTitle.trim()) {
      addNewCard(board.id, newCardTitle);
      setNewCardTitle('');
    }
    setIsAddingCard(false);
  };

  return (
    <div className="board">
      <div className="board-header">
        <h3>{board.title}</h3>
        <div className="board-menu-container">
          <button 
            className="board-menu-button" 
            onClick={() => setShowBoardMenu(!showBoardMenu)}
          >
            •••
          </button>
          {showBoardMenu && (
            <div className="board-menu">
              <div className="board-menu-item" onClick={() => deleteBoard(board.id)}>
                Delete list
              </div>
            </div>
          )}
        </div>
      </div>

      <Droppable droppableId={board.id}>
        {(provided) => (
          <div 
            className="cards-container"
            {...provided.droppableProps}
            ref={provided.innerRef}
          >
            {board.cards.map((card, index) => (
              <Card 
                key={card.id} 
                card={card} 
                index={index} 
                boardId={board.id}
                deleteCard={deleteCard}
              />
            ))}
            {provided.placeholder}
          </div>
        )}
      </Droppable>

      {isAddingCard ? (
        <div className="add-card-form">
          <textarea
            value={newCardTitle}
            onChange={(e) => setNewCardTitle(e.target.value)}
            placeholder="Enter a title for this card..."
            autoFocus
          />
          <div className="add-card-actions">
            <button onClick={handleAddCard}>Add Card</button>
            <button onClick={() => setIsAddingCard(false)}>Cancel</button>
          </div>
        </div>
      ) : (
        <div className="add-card" onClick={() => setIsAddingCard(true)}>
          <span>+ Add a card</span>
        </div>
      )}
    </div>
  );
}

export default Board;
