import React, { useState } from 'react';
import { Draggable } from 'react-beautiful-dnd';
import './Card.css';

function Card({ card, index, boardId, deleteCard, updateCard, toggleCardCompleted, toggleActivityCompleted, addCardActivity }) {
  const [showCardMenu, setShowCardMenu] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(card.title);
  const [editDescription, setEditDescription] = useState(card.description || '');
  const [showActivities, setShowActivities] = useState(false);
  const [newActivity, setNewActivity] = useState('');

  return (
    <Draggable draggableId={card.id} index={index}>
      {(provided) => (
        <div
          className={`card ${card.completed ? 'card-completed' : ''}`}
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
        >
          <div className="card-radio-container">
            <input 
              type="radio" 
              className="card-radio" 
              checked={card.completed} 
              onChange={(e) => {
                e.stopPropagation();
                toggleCardCompleted(boardId, card.id);
              }} 
            />
          </div>
          <div className="card-labels">
            {card.labels.map((label, i) => (
              <span key={i} className={`card-label label-${label.toLowerCase()}`}>
                {label}
              </span>
            ))}
          </div>
          {isEditing ? (
            <div className="card-edit-form">
              <textarea
                className="card-edit-title"
                value={editTitle}
                onChange={(e) => setEditTitle(e.target.value)}
                placeholder="Enter card title..."
                autoFocus
              />
              <textarea
                className="card-edit-description"
                value={editDescription}
                onChange={(e) => setEditDescription(e.target.value)}
                placeholder="Enter card description..."
              />
              <div className="card-edit-actions">
                <button 
                  className="card-edit-save" 
                  onClick={(e) => {
                    e.stopPropagation();
                    if (editTitle.trim()) {
                      updateCard(boardId, card.id, editTitle, editDescription);
                      setIsEditing(false);
                    }
                  }}
                >
                  Save
                </button>
                <button 
                  className="card-edit-cancel" 
                  onClick={(e) => {
                    e.stopPropagation();
                    setEditTitle(card.title);
                    setEditDescription(card.description || '');
                    setIsEditing(false);
                  }}
                >
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <div className="card-content">
              <div className="card-title">{card.title}</div>
              {card.description && (
                <div className="card-description">{card.description}</div>
              )}

              {card.activities && card.activities.length > 0 && (
                <div className="card-activities-summary">
                  <span onClick={() => setShowActivities(!showActivities)} className="card-activities-toggle">
                    Activities: {card.activities.filter(a => a.completed).length}/{card.activities.length}
                  </span>
                </div>
              )}

              {showActivities && (
                <div className="card-activities">
                  <h4>Activities</h4>
                  {card.activities.map(activity => (
                    <div key={activity.id} className="card-activity-item">
                      <input 
                        type="checkbox" 
                        checked={activity.completed} 
                        onChange={() => toggleActivityCompleted(boardId, card.id, activity.id)}
                      />
                      <span className={activity.completed ? 'completed' : ''}>{activity.text}</span>
                    </div>
                  ))}
                  <div className="card-add-activity">
                    <input 
                      type="text" 
                      value={newActivity}
                      onChange={(e) => setNewActivity(e.target.value)}
                      placeholder="Add a new activity..."
                    />
                    <button 
                      onClick={() => {
                        addCardActivity(boardId, card.id, newActivity);
                        setNewActivity('');
                      }}
                      disabled={!newActivity.trim()}
                    >
                      Add
                    </button>
                  </div>
                </div>
              )}
            </div>
          )}
          <div className="card-menu-container">
            <button 
              className="card-menu-button" 
              onClick={(e) => {
                e.stopPropagation();
                setShowCardMenu(!showCardMenu);
              }}
            >
              <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                <path d="M12 8c1.1 0 2-.9 2-2s-.9-2-2-2-2 .9-2 2 .9 2 2 2zm0 2c-1.1 0-2 .9-2 2s.9 2 2 2 2-.9 2-2-.9-2-2-2zm0 6c-1.1 0-2 .9-2 2s.9 2 2 2 2-.9 2-2-.9-2-2-2z"/>
              </svg>
            </button>
            {showCardMenu && (
              <div className="card-menu">
                <div 
                  className="card-menu-item" 
                  onClick={(e) => {
                    e.stopPropagation();
                    setIsEditing(true);
                    setShowCardMenu(false);
                  }}
                >
                  Edit card
                </div>

                <div 
                  className="card-menu-item" 
                  onClick={(e) => {
                    e.stopPropagation();
                    setShowActivities(!showActivities);
                    setShowCardMenu(false);
                  }}
                >
                  {showActivities ? 'Hide activities' : 'Show activities'}
                </div>

                <div 
                  className="card-menu-item" 
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteCard(boardId, card.id);
                    setShowCardMenu(false);
                  }}
                >
                  Delete card
                </div>
              </div>
            )}
          </div>
        </div>
      )}
    </Draggable>
  );
}

export default Card;
