import React, { useState } from 'react';
import { Draggable } from 'react-beautiful-dnd';
import './Card.css';

function Card({ card, index, boardId, deleteCard, updateCard, toggleCardCompleted }) {
  const [showCardMenu, setShowCardMenu] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(card.title);
  const [editDescription, setEditDescription] = useState(card.description || '');

  return (
    <Draggable draggableId={card.id} index={index}>
      {(provided) => (
        <div
          className={`card ${card.completed ? 'card-completed' : ''}`}
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
        >
          <div className="card-radio-container">
            <input 
              type="radio" 
              className="card-radio" 
              checked={card.completed} 
              onChange={(e) => {
                e.stopPropagation();
                toggleCardCompleted(boardId, card.id);
              }} 
            />
          </div>
          <div className="card-labels">
            {card.labels.map((label, i) => (
              <span key={i} className={`card-label label-${label.toLowerCase()}`}>
                {label}
              </span>
            ))}
          </div>
          {isEditing ? (
            <div className="card-edit-form">
              <textarea
                className="card-edit-title"
                value={editTitle}
                onChange={(e) => setEditTitle(e.target.value)}
                placeholder="Enter card title..."
                autoFocus
              />
              <textarea
                className="card-edit-description"
                value={editDescription}
                onChange={(e) => setEditDescription(e.target.value)}
                placeholder="Enter card description..."
              />
              <div className="card-edit-actions">
                <button 
                  className="card-edit-save" 
                  onClick={(e) => {
                    e.stopPropagation();
                    if (editTitle.trim()) {
                      updateCard(boardId, card.id, editTitle, editDescription);
                      setIsEditing(false);
                    }
                  }}
                >
                  Save
                </button>
                <button 
                  className="card-edit-cancel" 
                  onClick={(e) => {
                    e.stopPropagation();
                    setEditTitle(card.title);
                    setEditDescription(card.description || '');
                    setIsEditing(false);
                  }}
                >
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <div className="card-content">
              <div className="card-title">{card.title}</div>
              {card.description && (
                <div className="card-description">{card.description}</div>
              )}
              {card.members && card.members.length > 0 && (
                <div className="card-members">
                  {card.members.map(memberId => {
                    const member = teamMembers.find(m => m.id === memberId);
                    return member ? (
                      <span key={memberId} className="card-member-avatar" title={member.name}>
                        {member.avatar}
                      </span>
                    ) : null;
                  })}
                </div>
              )}
            </div>
          )}
          <div className="card-menu-container">
            <button 
              className="card-menu-button" 
              onClick={(e) => {
                e.stopPropagation();
                setShowCardMenu(!showCardMenu);
              }}
            >
              <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                <path d="M12 8c1.1 0 2-.9 2-2s-.9-2-2-2-2 .9-2 2 .9 2 2 2zm0 2c-1.1 0-2 .9-2 2s.9 2 2 2 2-.9 2-2-.9-2-2-2zm0 6c-1.1 0-2 .9-2 2s.9 2 2 2 2-.9 2-2-.9-2-2-2z"/>
              </svg>
            </button>
            {showCardMenu && (
              <div className="card-menu">
                <div 
                  className="card-menu-item" 
                  onClick={(e) => {
                    e.stopPropagation();
                    setIsEditing(true);
                    setShowCardMenu(false);
                  }}
                >
                  Edit card
                </div>
                <div 
                  className="card-menu-item" 
                  onClick={(e) => {
                    e.stopPropagation();
                    setShowMemberSelector(!showMemberSelector);
                    setShowCardMenu(false);
                  }}
                >
                  Assign members
                </div>
                <div 
                  className="card-menu-item" 
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteCard(boardId, card.id);
                    setShowCardMenu(false);
                  }}
                >
                  Delete card
                </div>
              </div>
            )}
          </div>
          {showMemberSelector && (
            <div className="card-member-selector">
              <div className="card-member-selector-title">Assign Members</div>
              <div className="card-member-selector-list">
                {teamMembers.map(member => {
                  const isAssigned = card.members.includes(member.id);
                  return (
                    <div 
                      key={member.id} 
                      className={`card-member-selector-item ${isAssigned ? 'selected' : ''}`}
                      onClick={(e) => {
                        e.stopPropagation();
                        toggleCardMember(boardId, card.id, member.id);
                      }}
                    >
                      <span className="card-member-selector-avatar">{member.avatar}</span>
                      <span className="card-member-selector-name">{member.name}</span>
                      {isAssigned && (
                        <span className="card-member-selector-check">âœ“</span>
                      )}
                    </div>
                  );
                })}
              </div>
              <button 
                className="card-member-selector-close" 
                onClick={(e) => {
                  e.stopPropagation();
                  setShowMemberSelector(false);
                }}
              >
                Close
              </button>
            </div>
          )}
        </div>
      )}
    </Draggable>
  );
}

export default Card;
