import React, { useState } from 'react';
import './BoardSwitcher.css';

function BoardSwitcher({ onBoardSwitch }) {
  const [activeBoard, setActiveBoard] = useState('My Board');

  const boards = [
    { id: 'my-board', name: 'My Board' },
    { id: 'planner', name: 'Planner' },
    { id: 'inbox', name: 'Inbox' }
  ];

  const handleBoardSwitch = (boardId, boardName) => {
    setActiveBoard(boardName);
    if (onBoardSwitch) {
      onBoardSwitch(boardId);
    }
  };

  return (
    <div className="board-switcher">
      <div className="board-switcher-container">
        {boards.map(board => (
          <div 
            key={board.id}
            className={`board-option ${activeBoard === board.name ? 'active' : ''}`}
            onClick={() => handleBoardSwitch(board.id, board.name)}
          >
            {board.name}
          </div>
        ))}
      </div>
    </div>
  );
}

export default BoardSwitcher;
